import {takeLatest , call ,put , all } from 'redux-saga/effects';
import axios from 'axios';
import SHOP_ACTION_TYPES from './shop.types';
import {getCollectionSuccess} from './shop.action';

export function fetchCollectionToServer(limit = 10,page = 1) {
    return axios(`http://localhost:2222/product?limit=${limit}&page=${page}`,{
        method: "get",
    })
}



export function* fetchCollectionAsync({payload}) {
    const {limit , page} = payload;
    yield console.log(limit , page)
    const {data : {data : {products}}} = yield call(fetchCollectionToServer,limit,page);

    yield put(getCollectionSuccess(products))
}



export function* fetchCollectionsStart() {
    yield takeLatest(SHOP_ACTION_TYPES.GET_COLLECTIONS_START,fetchCollectionAsync)
}


export function* shopSagas () {
    yield all([call(fetchCollectionsStart)])
}
