import React from 'react';
import {
  Layout, Menu,
} from 'antd';


import { ReactComponent as Paint } from '../../assets/001-pant.svg';
import { ReactComponent as Shirt } from '../../assets/002-shirt.svg';
import { ReactComponent as Shoes } from '../../assets/003-shoes.svg';

const { SubMenu } = Menu;
const { Sider } = Layout;
class SideBarHome extends React.Component {
  state =  {
    collapsed: false,
  }
  onCollapse = collapsed => {
    console.log(collapsed);
    this.setState({ collapsed });
  };
  render(){
    return(
      <Sider collapsible collapsed={this.state.collapsed} onCollapse={this.onCollapse} width={200} className="site-layout-background">
      <Menu
        mode="inline"
        defaultSelectedKeys={['1']}

        style={{ height: '100%', borderRight: 0 }}
      >
        <SubMenu key="sub1" icon={<Shoes style={{ width: '15px', height: '15px', marginRight: '5px' }} />} title="Shoes">
          <Menu.Item key="1">Adidas</Menu.Item>
          <Menu.Item key="2">NiKe</Menu.Item>
          <Menu.Item key="3">Vans</Menu.Item>
          <Menu.Item key="4">Chelsea Boots</Menu.Item>
        </SubMenu>
        <SubMenu key="sub2" icon={<Paint style={{ width: '15px', height: '15px', marginRight: '5px' }} />} title="Pants">
          <Menu.Item key="5">Jeans</Menu.Item>
          <Menu.Item key="6">Shorts</Menu.Item>
          <Menu.Item key="7">Skirt</Menu.Item>
          <Menu.Item key="8">Trousers</Menu.Item>
        </SubMenu>
        <SubMenu key="sub3" icon={<Shirt style={{ width: '15px', height: '15px', marginRight: '5px' }} />} title="Clothes">
          <Menu.Item key="9">Hoodie</Menu.Item>
          <Menu.Item key="10">T-Shirt</Menu.Item>
          <Menu.Item key="11">Jacket</Menu.Item>
          <Menu.Item key="12">Vest</Menu.Item>
          <Menu.Item key="13">Sweater</Menu.Item>
        </SubMenu>
      </Menu>
    </Sider>
    )
  }
}


export default SideBarHome;
