import SHOP_ACTION_TYPES from './shop.types';
const INITIAL_STATE = {
  collections: [],
  isLoading : true
};

const shopReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case SHOP_ACTION_TYPES.GET_COLLECTIONS_SUCCESS:
      return {
        ...state,
        collections: action.payload
      }
    default:
      return state;
  }
};

export default shopReducer;
