import React from 'react';
import windowSize from 'react-window-size';

import { Link } from 'react-router-dom';
import { UserOutlined, ShopOutlined, LogoutOutlined } from '@ant-design/icons';
import { Menu } from 'antd';

const DropdownUser = ({ windowWidth }) => (
  <Menu
    mode={windowWidth <= 767 ? 'vertical' : 'horizontal'}
    style={{ marginTop: '20px' }}
  >
    <Menu.Item key="0" icon={<UserOutlined style={{ fontSize: '16px' }} />}>
      <Link to="/user">Your Profile</Link>
    </Menu.Item>
    <Menu.Item key="1" icon={<ShopOutlined style={{ fontSize: '16px' }} />}>
      <Link to="/shop">Your Shop</Link>
    </Menu.Item>
    <Menu.Divider />
    <Menu.Item key="3" icon={<LogoutOutlined style={{ fontSize: '16px' }} />}>Log out</Menu.Item>
  </Menu>
);

export default windowSize(DropdownUser);
