import React from 'react';
import { Card, Button } from 'antd';
import { MoreOutlined, ShoppingCartOutlined } from '@ant-design/icons';
import './card-item.styles.scss';
import CardItemContent from '../card-item-content/card-item-content.component';

const { Meta } = Card;

const CardItem = ({ cartItem }) => (
  <Card
    className="card-container"
    hoverable
    cover={(
      <img
        alt="example"
        className="card-image"
        style={{ width: '100%' }}
        src={cartItem.photoURL}
      />
    )}
    actions={[
      <Button key="submit" type="primary" icon={<  ShoppingCartOutlined />}>

        Add To Card
      </Button>,
      <Button key="submit" type="secondary" icon={<MoreOutlined />}>

        More Detail
      </Button>,
    ]}
  >
    <Meta
      title={<span style={{ fontSize: '1.6rem' }}>{cartItem.nameEN}</span>}
      description={
        <CardItemContent cartItem={cartItem} />
      }
    />
  </Card>
);

export default CardItem;
