import React from 'react';
import {
  Layout, Modal, Tabs,
} from 'antd';
import windowSize from 'react-window-size';

import { ReactComponent as LoginIcon } from '../../assets/001-login.svg';
import { ReactComponent as Register } from '../../assets/002-escalator.svg';
import SignIn from '../../components/sign-in/sign-in.component';
import SignUp from '../../components/sign-up/sign-up.component';
import './sign-in-sign-up.styles.scss';

const { Content } = Layout;
const { TabPane } = Tabs;
function callback(key) {
  console.log(key);
}
const SignInSignUpPage = ({ windowWidth }) => {
  const handleCancel = (e) => {
    console.log(e);
  };
  const handleOk = (e) => {
    console.log(e);
  };
  return (
    <>
      <Content
        className="containerSignInSignUp"

      >
        <Modal
          visible
          onOk={handleOk}
          footer={null}
          onCancel={handleCancel}
        >
          <Tabs
            defaultActiveKey="1"
            onChange={callback}
            size={windowWidth <= 900 ? 'small' : 'large'}

            centered="true"
            tabBarGutter={50}
          >
            <TabPane
              tab={(
                <span style={{ display: 'flex', justifyContent: 'center' }}>
                  <LoginIcon style={{ width: '25px', height: '25px' }} />
                  &nbsp;

                  Log In
                </span>
              )}
              key="1"
            >
              <SignIn />
            </TabPane>
            <TabPane
              tab={(
                <span style={{ display: 'flex', justifyContent: 'center' }}>
                  <Register style={{ width: '25px', height: '25px' }} />
                  &nbsp;

                  Sign Up
                </span>
              )}
              key="2"
            >
              <SignUp />
            </TabPane>
          </Tabs>
        </Modal>

      </Content>

    </>
  );
};

export default windowSize(SignInSignUpPage);
