import {takeLatest, all, call,put} from 'redux-saga/effects';
import axios from 'axios';
import USER_ACTIONS_TYPES from '../user/user.types';
import {setCurrentUser} from '../user/user.action';

export function fetchUserFormToServer (data,type) {
    return axios(`http://localhost:2222/user/${type}`,{
        method: "post",
        data: data
    })
}


export function * signUp({payload}) {
   const {data : {data : {user}}} = yield call(fetchUserFormToServer,payload,"register");
   yield put(setCurrentUser(user))
}

export function* signIn({payload}) {
    const {data : {data : {user}}} = yield call(fetchUserFormToServer,payload, "signIn");
    yield put(setCurrentUser(user));
}


export function* onSignUpStart() {
    yield takeLatest(USER_ACTIONS_TYPES.SIGN_UP_START,signUp);
}
export function* onSignInStart() {
    yield takeLatest(USER_ACTIONS_TYPES.SIGN_UP_START,signIn)
}

export function* userSagas() {
    yield all([
        call(onSignUpStart),
        call(onSignInStart)
    ])
}
