import React , {useState} from 'react';
import SuccessCheckout from './success-checkout.component';
import { Modal , Table } from 'antd';
import { createStructuredSelector } from 'reselect';
import { connect } from 'react-redux';
import { selectHiddenModelCheckOut } from '../../../redux/cart/cart.selector';
import {columns} from './model-checkout-prefix-layout';
import { toggleCheckoutHidden } from '../../../redux/cart/cart.action';
import { selectCurrentUser } from '../../../redux/user/user.selector';
import ModelForm from './model-form.component';
import { checkOutStart } from '../../../redux/check-out/check-out.action';
import { selectSuccess } from '../../../redux/check-out/check-out.selector';

// HANDLE TABLE EVENT



const ModelCheckout = ({hiddenModelCheckOut,success,toggleCheckoutHidden,currentUser,checkOutStart,...props}) => {
  const [addressShip , setAddressShip] = useState(" ")
  const rowSelection = {
    onChange: (selectedRowKeys, selectedRows) => {
      setAddressShip(selectedRows[0].name)

    }
  };
  return (
    <>
  <Modal
    width={650}
    title="Please Choose Address"
    visible={hiddenModelCheckOut}
    footer = {null}
    okText= "Complete Shopping"
    okButtonProps= {{ style: { display: 'block' } }}
    onCancel={() => toggleCheckoutHidden()}
    onOk = {() => checkOutStart(addressShip)}
  >
    {
      !success ? (
        <>
          <Table
          pagination={false}
          rowSelection={{
            type: "radio",
            ...rowSelection
          }}
          rowKey='_id'
          columns={columns}
          dataSource = {currentUser.address}
          />
            <br/>
            <ModelForm/>
        </>
      ) : (<SuccessCheckout/>)
    }
  </Modal>

  </>
)};

const mapStateProps = createStructuredSelector({
  hiddenModelCheckOut: selectHiddenModelCheckOut,
  currentUser : selectCurrentUser,
  success: selectSuccess
});
const mapDispatchToProps = dispatch => ({
  toggleCheckoutHidden : () => dispatch(toggleCheckoutHidden()),
  checkOutStart : address => dispatch(checkOutStart(address))
})

export default connect(mapStateProps,mapDispatchToProps)(ModelCheckout);
