import React from 'react';
import {connect} from 'react-redux';
import { Layout, Table,Skeleton } from 'antd';
import COLUMNS from './column-content';
import { createStructuredSelector } from 'reselect';
import { selectCurrentUser } from '../../redux/user/user.selector';
import { getBookingStart } from '../../redux/booking/booking-action';
import { selectHistoryBooking, selectIsLoadingBOOKING } from '../../redux/booking/booking-selector';

const { Content } = Layout;



class BookingContent extends React.Component {
  state = {
    columnsPrefix : [],
  }
  componentDidMount() {
    const {currentUser,getBookingStart} = this.props;
    if(!currentUser.roles.includes("admin") ) {
      let newState = COLUMNS.filter(el => el.title !== 'isComplete');
      this.setState({
        columnsPrefix : newState
      }) 
    } else {
      this.setState({
        columnsPrefix : COLUMNS
      })   
    }

    console.log('vao day con trai')
    getBookingStart();  
  }
  componentDidUpdate(){
    console.log("!!")
  }
  handleChange = (selectedRowKeys ,selectedRows ) => {
    console.log(
      `selectedRowKeys: ${selectedRowKeys}`,
      'selectedRows: ',
      selectedRows,
    );
  }



  render() {
    const {columnsPrefix} = this.state;
    const {historyBooking,isLoading} = this.props;

    if(isLoading)  {
      return (
        <Layout style={{ padding: '0 24px 24px' }}>
          <Content style={{
            padding: 24,
            margin: 0,
            minHeight: 600,
          }}
          >
            <div>
              <Table
                pagination={false}
                tableLayout="fixed"
                rowSelection={{
                  type:'radio'
                }}
                onChange = {this.handleChange}
                scroll={{ x: 2000 }}
                title={() => 'Booking Pages'}
                dataSource={historyBooking}
                columns={columnsPrefix}
                rowKey='key'
              /> 
            </div>
          </Content>
        </Layout>
      )
    }
    else {
      return (
        <Skeleton paragraph={{ rows: 6 }}/>
      )
    }


  }
}

const mapStateToProps = createStructuredSelector({
  currentUser : selectCurrentUser,
  historyBooking : selectHistoryBooking,
  isLoading : selectIsLoadingBOOKING
})

const mapDispatchToProps = dispatch => ({
  getBookingStart: () => dispatch(getBookingStart())
})
export default connect( mapStateToProps,mapDispatchToProps)(BookingContent);
