import React from 'react';
import {
  Layout, Menu,
} from 'antd';
import windowSize from 'react-window-size';
import dataSideBar from './sidebar-home-data';
import { ReactComponent as Shirt } from '../../assets/002-shirt.svg';

import './sidebar-home.style.scss';

const { SubMenu } = Menu;
const { Sider } = Layout;

const SideBarHome = ({ windowWidth }) => (
  <Sider
    collapsed={windowWidth <= 900}
    style={windowWidth <= 900 ? { width: '5rem' } : ''}
    className="site-layout-background"
  >
    <Menu
      mode="inline"
      defaultSelectedKeys={['1']}
      style={{ height: '100%', borderRight: 0 }}
    >
      <SubMenu key="sub3" icon={<Shirt className="icon-sidebar-home" />} title={<span className="title-category">Clothes</span>}>
        {
          dataSideBar.map((el,idx) =>  <Menu.Item key={idx}>{el}</Menu.Item>)
        }


      </SubMenu>
    </Menu>
  </Sider>
);

export default windowSize(SideBarHome);
