import React , {useState} from 'react';
import { MinusCircleOutlined, PlusOutlined } from "@ant-design/icons";

import { Modal ,Form, Input, Button, Radio , Table } from 'antd';
import { createStructuredSelector } from 'reselect';
import { connect } from 'react-redux';
import { selectHiddenModelCheckOut } from '../../../redux/cart/cart.selector';
import {formItemLayout,formItemLayoutWithOutLabel} from './model-checkout-prefix-layout';
import { toggleCheckoutHidden } from '../../../redux/cart/cart.action';
import { selectCurrentUser } from '../../../redux/user/user.selector';
const columns = [
  {
    title: "Address Ship",
    dataIndex: "address"
  }
];

const ModelCheckout = ({hiddenModelCheckOut,toggleCheckoutHidden,currentUser,...props}) => {
  const onFinish = (values) => {
    console.log("Received values of form:", values);
  };
  return (
  <Modal
    width={650}
    title="Please Choose Address"
    visible={hiddenModelCheckOut}
    footer={null}
    onCancel={() => toggleCheckoutHidden()}
  >
    <Table
        rowSelection={{
          type: "radio",
          ...rowSelection
        }}
        columns={columns}
      />
      <Button onClick={() => setVisible(!visiable)}>Add New Adress</Button>
      <Modal title="Add Your New Address" visible={visiable}>
        <Form>
          <Input style={{ width: "60%" }} />
        </Form>
      </Modal>
  </Modal>
)};

const mapStateProps = createStructuredSelector({
  hiddenModelCheckOut: selectHiddenModelCheckOut,
  currentUser : selectCurrentUser
});
const mapDispatchToProps = dispatch => ({
  toggleCheckoutHidden : () => dispatch(toggleCheckoutHidden())
})

export default connect(mapStateProps,mapDispatchToProps)(ModelCheckout);
