import React from 'react';
import { withRouter } from 'react-router-dom';
import { connect } from 'react-redux';
import { Button } from 'antd';
import { toggleCartHidden } from '../../redux/cart/cart.action';
import './card-dropdown.styles.scss';

const CartDropDown = ({ history,toggleCartHidden }) => (
  <div className="cart-dropdown">
    <div className="cart-items">
      <span className="empty-message">Your cart is empty</span>
    </div>
    <Button type="primary" onClick={() => {history.push('/checkout');toggleCartHidden()}}>GO TO CHECKOUT</Button>
  </div>
);
const mapDispatchToProps = dispatch => ({
  toggleCartHidden : () => dispatch(toggleCartHidden())
})

export default withRouter(connect(null,mapDispatchToProps)(CartDropDown));
