import React from 'react';
import { connect } from 'react-redux';
import { selectCollections } from '../../redux/shop/shop.selector';

import './collection-page.styles.scss';
import {
  Layout, Input, Row, Col,
} from 'antd';
import CardItem from '../../components/card-item/card-item.component';
import { createStructuredSelector } from 'reselect';
import {getCollectionStart} from '../../redux/shop/shop.action';
const { Content } = Layout;
const { Search } = Input;

class CollectionPage extends React.Component {


  componentDidMount() {
    const {getCollectionStart} = this.props;
    getCollectionStart();

  }
  render() {
    const {collections,match,history} = this.props;
    return (
      <Layout style={{ padding: '0 2.4rem 2.4rem' }}>
        <Search
          placeholder="Search Category ... "
          enterButton
          size="large"
          style={{ marginBottom: '2rem' }}
        />
        <h2>Product  Pages</h2>
        <Content
          className="site-layout-background"
          style={{
            padding: 24,
            margin: 0,
            minHeight: 600,
          }}
        >
          <Row gutter={[48, 24]}>
            {
              collections.slice(0, 3).map((cartItem, idx) => (
                <Col key={idx} xs={24} sm={12} md={8} lg={6} className="gutter-row">
                  <CardItem key={idx} cartItem={cartItem} match= {match} history={history} {...this.props} />
                </Col>
              ))
            }
          </Row>
        </Content>
    </Layout>
    )
  }
}

const mapStateToProps = createStructuredSelector({
  collections: selectCollections,
});

const mapDispatchToProps = dispatch => ({
  getCollectionStart : () => dispatch(getCollectionStart())
})

export default connect(mapStateToProps)(CollectionPage);
