import React from 'react';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import windowSize from 'react-window-size';
import { createStructuredSelector } from 'reselect';
import {
  ShoppingOutlined, HomeOutlined, ShopOutlined, LoginOutlined,
} from '@ant-design/icons';
import { Menu } from 'antd';
import { selectCurrentUser } from '../../redux/user/user.selector';
import { toggleCartHidden } from '../../redux/cart/cart.action';
import { selectCartHidden } from '../../redux/cart/cart.selector';
import CartDropDown from '../card-dropdown/card-dropdown.component';
import NavigationUser from './navigation-user.component';

const RightNavigation = ({
  windowWidth, currentUser, toggleCartHidden, hidden,
}) => (

  <Menu
    theme="dark"
    mode={windowWidth <= 900 ? 'inline' : 'horizontal'}
    defaultSelectedKeys={['1']}
  >
    <Menu.Item
      style={{ height: '100%' }}
      key="1"
      icon={(
        <Link to="/">
          <HomeOutlined style={{ fontSize: '2.5rem', paddingTop: '1rem' }} />
          Home
        </Link>
      )}
    >
      {' '}

    </Menu.Item>
    <Menu.Item
      style={{ height: '100%' }}
      icon={(
        <Link to="/productQuery">
          <ShopOutlined
            style={{ fontSize: '2.5rem', paddingTop: '1rem' }}
          />
          Product
        </Link>
        )}
      key="2"
    />
    <Menu.Item
      style={{ height: '100%' }}
      icon={(
        <span>
          <ShoppingOutlined
            style={{ fontSize: '2.5rem', paddingTop: '1rem' }}
          />
          Cart
        </span>

        )}
      key="3"
    />
    {
      currentUser ? <NavigationUser currentUser={currentUser} /> : (
        <Menu.Item
          style={{ height: '100%' }}
          icon={(
            <Link to="/signInSignUp">
              <LoginOutlined
                style={{ fontSize: '2.5rem', paddingTop: '1rem' }}
              />
              Log In
            </Link>
        )}
          key="4"
        />
      )
    }
    <CartDropDown style={hidden ? { display: 'block' } : ' '} />
  </Menu>
);

const mapStateToProps = createStructuredSelector({
  currentUser: selectCurrentUser,
  hidden: selectCartHidden,
});

const mapDispatchToProps = (dispatch) => ({
  toggleCartHidden: () => dispatch(toggleCartHidden()),
});

export default connect(mapStateToProps, mapDispatchToProps)(windowSize(RightNavigation));
