import React from 'react';
import {connect} from 'react-redux';

import { Button } from 'antd';
import './pay-checkout.styles.scss';
import { createStructuredSelector } from 'reselect';
import { selectTotalPrice } from '../../../redux/cart/cart.selector';

const PayCheckOut = ({totalItem}) => (
  <div className="pay-container">
    <h2>Order Summary</h2>
    <div>
      <p>Total : </p>
      <p>{totalItem}$</p>
    </div>
    <Button type="primary" style={{ width: '100%' }}>Check Out</Button>
  </div>
);

const mapStateToProps = createStructuredSelector({
  totalItem : selectTotalPrice
})

export default connect(mapStateToProps)(PayCheckOut);
