import React from 'react';
import {connect} from 'react-redux';
import { Layout, Table } from 'antd';
import COLUMNS from './column-content';
import DATA from './data';
import { createStructuredSelector } from 'reselect';
import { selectCurrentUser } from '../../redux/user/user.selector';

const { Content } = Layout;



class BookingContent extends React.Component {
  state = {
    columnsPrefix : []
  }
  componentDidMount() {
    const {currentUser} = this.props;
    const {columnsPrefix} = this.state;
    if(!currentUser.roles.includes("admin") ) {
      let newState = COLUMNS.filter(el => el.title !== 'isComplete')
      this.setState({
        columnsPrefix : newState
      }) 
    }
    this.setState({
      columnsPrefix : COLUMNS
    })    

  }
  handleChange = (selectedRowKeys ,selectedRows ) => {
    console.log(
      `selectedRowKeys: ${selectedRowKeys}`,
      'selectedRows: ',
      selectedRows,
    );
  }

  render() {
    const {columnsPrefix} = this.state;

    // if(!currentUser.roles.includes("admin") ) {
    //   COLUMNS = COLUMNS.filter(el => el.title !== 'isComplete')
    // }
    return(
      <Layout style={{ padding: '0 24px 24px' }}>
      <Content style={{
        padding: 24,
        margin: 0,
        minHeight: 600,
      }}
      >
        <div>
          <Table
            pagination={false}
            tableLayout="fixed"
            onChange = {this.handleChange}
            scroll={{ x: 2000 }}
            title={() => 'Booking Pages'}
            dataSource={DATA}
            columns={columnsPrefix}
          />
        </div>
      </Content>
    </Layout>
    )
  }
}

const mapStateToProps = createStructuredSelector({
  currentUser : selectCurrentUser
})
export default connect( mapStateToProps)(BookingContent);
