import {takeLatest, all, call,put , select} from 'redux-saga/effects';
import axios from 'axios';
import USER_ACTIONS_TYPES from '../user/user.types';
import {setCurrentUser,signOutSuccess} from '../user/user.action';
import {selectCurrentUser} from './user.selector';
import { selectCartItem, selectTotalPrice } from '../cart/cart.selector';

export function fetchUserToServer (data,type) {
    return axios(`http://localhost:2222/user/${type}`,{
        method: "post",
        data: data
    })
}

export function fetchBookingToServer (data) {
    let token = "Bear " + JSON.parse(localStorage.getItem("login"));
    return axios(`http://localhost:2222/booking`, {
        method : "POST",
        headers : {
            'Authorization': token
        }, 
    })
}


export function* createBooking({payload}) {
    yield console.log(payload);
    const currentUser = yield select(selectCurrentUser);
    const cartItem = yield select(selectCartItem);
    const totalMoney = yield select(selectTotalPrice);
    const data = Object.assign({} , {
        cart:cartItem,
        totalMoney,
        address: payload,
        idUser : currentUser._id
    })
}

export function * signUp({payload}) {
   const {data : {data : {user}}} = yield call(fetchUserToServer,payload,"register");
   yield put(setCurrentUser(user))
}

export function* signIn({payload}) {
    const {data : {data : {user}}} = yield call(fetchUserToServer,payload, "signIn");
    yield put(setCurrentUser(user));
}

export function* signOut() {
    localStorage.removeItem("login")
    yield put(signOutSuccess())
}

export function* onSignOutStart() {
    yield takeLatest(USER_ACTIONS_TYPES.SIGN_OUT_START, signOut)
}


export function* onSignUpStart() {
    yield takeLatest(USER_ACTIONS_TYPES.SIGN_UP_START,signUp);
}
export function* onSignInStart() {
    yield takeLatest(USER_ACTIONS_TYPES.SIGN_IN_START,signIn)
}

export function* onCreateBookingStart() {
    yield takeLatest(USER_ACTIONS_TYPES.CREATE_BOOKING_START, createBooking)
}

export function* userSagas() {
    yield all([
        call(onSignUpStart),
        call(onSignInStart),
        call(onSignOutStart),
        call(onCreateBookingStart)
    ])
}
