import React from 'react';
import { MinusCircleOutlined, PlusOutlined } from "@ant-design/icons";

import { Modal ,Form, Input, Button, Radio } from 'antd';
import { createStructuredSelector } from 'reselect';
import { connect } from 'react-redux';
import { selectHiddenModelCheckOut } from '../../../redux/cart/cart.selector';
import {formItemLayout,formItemLayoutWithOutLabel} from './model-checkout-prefix-layout';
import { toggleCheckoutHidden } from '../../../redux/cart/cart.action';
import { selectCurrentUser } from '../../../redux/user/user.selector';
const ModelCheckout = ({hiddenModelCheckOut,toggleCheckoutHidden,currentUser,...props}) => {
  const onFinish = (values) => {
    console.log("Received values of form:", values);
  };
  return (
  <Modal
    width={650}
    title="Please Choose Address"
    visible={hiddenModelCheckOut}
    footer={null}
    onCancel={() => toggleCheckoutHidden()}
  >
    <Form
      name="dynamic_form_item"
      {...formItemLayoutWithOutLabel}
      onFinish={onFinish}
      initialValues = {{address: [...currentUser.address]}}
    >
      <Form.List name="names">
        {(fields, { add, remove }) => {
          return (
            <div>
              {fields.map((field, index) => (
                <Form.Item
                  {...(index === 0
                    ? formItemLayout
                    : formItemLayoutWithOutLabel)}
                  label={ `Address ${index + 1}` }
                  required={false}
                  key={field.key}
                >
                  <Form.Item
                    {...field}
                    validateTrigger={["onChange", "onBlur"]}
                    rules={[
                      {
                        required: true,
                        whitespace: true,
                        message:
                          "Please input Add Adress or Delete this Adress."
                      }
                    ]}
                    noStyle
                  >
                    <Input
                      placeholder="Address details"
                      style={{ width: "60%" }}
                    />
                  </Form.Item>
                  {fields.length >= 1 ? (
                    <>
                      <Radio style={{ marginLeft: "8px" }}>
                        Choose Address
                      </Radio>
                      <MinusCircleOutlined
                        onClick={() => {
                          remove(field.name);
                        }}
                      />
                    </>
                  ) : null}
                </Form.Item>
              ))}
              <Form.Item>
                <Button
                  type="dashed"
                  onClick={() => {
                    add();
                  }}
                  style={{ width: "60%" }}
                >
                  <PlusOutlined /> Add field
                </Button>
              </Form.Item>
            </div>
          );
        }}
      </Form.List>

      <Form.Item>
        <Button type="primary" htmlType="submit">
          Submit
        </Button>
      </Form.Item>
    </Form>
  </Modal>
)};

const mapStateProps = createStructuredSelector({
  hiddenModelCheckOut: selectHiddenModelCheckOut,
  currentUser : selectCurrentUser
});
const mapDispatchToProps = dispatch => ({
  toggleCheckoutHidden : () => dispatch(toggleCheckoutHidden())
})

export default connect(mapStateProps,mapDispatchToProps)(ModelCheckout);
