import React from 'react';
import { withRouter } from 'react-router-dom';
import { connect } from 'react-redux';
import { Button } from 'antd';
import { toggleCartHidden } from '../../redux/cart/cart.action';
import { selectCartItem } from '../../redux/cart/cart.selector';
import ItemDropdown from './item-dropdown.component';
import './card-dropdown.styles.scss';
import { createStructuredSelector } from 'reselect';

const CartDropDown = ({ history, toggleCartHidden, cartItems }) => (
  <div className="cart-dropdown">
    <div className="cart-items" style={cartItems.length >= 3 ? { overflowY: 'scroll', height: '100%' } : ''}>
      {
        !cartItems.length ? (<span className="empty-message">Your cart is empty</span>)
          : cartItems.map((el,idx) => <ItemDropdown cartItem={el} key ={idx}/>)
      }
    </div>
    <Button type="primary" onClick={() => { history.push('/checkout'); toggleCartHidden(); }}>GO TO CHECKOUT</Button>
  </div>
);
const mapDispatchToProps = (dispatch) => ({
  toggleCartHidden: () => dispatch(toggleCartHidden()),
});
const mapStateToProps = createStructuredSelector({
  cartItems: selectCartItem,
});

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(CartDropDown));
