import {takeLatest , call ,put , all } from 'redux-saga/effects';
import axios from 'axios';
import SHOP_ACTION_TYPES from './shop.types';


export function fetchCollectionToServer() {
    return axios(`http://localhost:2222/product`,{
        method: "get",
    })
}



export function* fetchCollectionAsync() {
    const response = yield call(fetchCollectionToServer);

    yield console.log(response);
}



export function* fetchCollectionsStart() {
    yield takeLatest(SHOP_ACTION_TYPES.GET_COLLECTIONS_START,fetchCollectionAsync)
}




export function* shopSagas () {
    yield all([cal(fetchCollectionsStart)])
}
