import React from 'react';
import {connect} from 'react-redux'
import './product-detail-page.styles.scss';
import { Button, Layout } from 'antd';
import { ShoppingCartOutlined } from '@ant-design/icons';
import { selectProductDetail } from '../../redux/shop/shop.selector';

const { Content } = Layout;

const ProductDetailPage = ({productDetail}) => {
  console.log(productDetail)
  return(

  <Content style={{
    padding: 24,
    margin: 0,
    minHeight: 600,
  }}
  >
    <h1>Product Detail Pages</h1>
    <div className="product-detail-container">
      <div className="product-image">
        <img width="100%" src="https://i.postimg.cc/WzYb4yFc/victor-vorontsov-29-Rj8-Bfj-TU-unsplash.jpg" alt="PRODUCT_DETAIL" />
      </div>
      <div className="product-detail">
        <h2>Description Product : </h2>
        <div className="item-detail">
          <h3>
            Category : Clothes
          </h3>
          <h3>Item Description :</h3>
          <p>
            Áo Sơ Mi Nữ trắng tay bồng, có lót vai, không nhãn hiệu , mới 100%
          </p>
          <h3>Order Net Weight: </h3>
          <span>1.82</span>
          <h3>Order Gross Weight: </h3>
          <span>1.83</span>
          <h3>Item Factory Country:</h3>
          <span>CN</span>
          <br />
          <br />
          <br />
        </div>
        <div>
          <Button type="primary" style={{ width: '100%', fontSize: '24px', height: '100px' }}>
            <ShoppingCartOutlined />
            {' '}
            Buy Now 16$
          </Button>

        </div>
      </div>
    </div>
  </Content>
)};

const mapStateToProps = (state, ownProps) => ({
  productDetail: selectProductDetail(ownProps.match.params.id)(state),
});

export default connect(mapStateToProps)(ProductDetailPage);
