import React from 'react';
import { Link } from 'react-router-dom';
import windowSize from 'react-window-size';
import {
  ShoppingOutlined, HomeOutlined, ShopOutlined, UserOutlined,
  LogoutOutlined,
} from '@ant-design/icons';
import { Menu } from 'antd';
import CartDropDown from '../card-dropdown/card-dropdown.component';

const { SubMenu } = Menu;

const RightNavigation = ({ windowWidth }) => (

  <Menu
    theme="dark"
    mode={windowWidth <= 600 ? 'inline' : 'horizontal'}
    defaultSelectedKeys={['1']}
  >
    <Menu.Item
      style={{ height: '100%' }}
      key="1"
      icon={(
        <Link to="/">
          <HomeOutlined style={{ fontSize: '2.5rem', paddingTop: '1rem' }} />
          Home
        </Link>
      )}
    >
      {' '}

    </Menu.Item>
    <Menu.Item
      style={{ height: '100%' }}
      icon={(
        <Link to="/productQuery">
          <ShopOutlined
            style={{ fontSize: '2.5rem', paddingTop: '1rem' }}
          />
          Product
        </Link>
        )}
      key="2"
    />
    <Menu.Item
      style={{ height: '100%' }}
      icon={(
        <Link to="/checkout">
          <ShoppingOutlined
            style={{ fontSize: '2.5rem', paddingTop: '1rem' }}
          />
          Cart
        </Link>
        )}
      key="3"
    />
    <SubMenu icon={<UserOutlined />} title="Thuong Le">

      <Menu.Item key="4" icon={<UserOutlined style={{ fontSize: '1.6rem' }} />}>
        <Link to="/user">Your Profile</Link>
      </Menu.Item>
      <Menu.Item key="5" icon={<ShopOutlined style={{ fontSize: '1.6rem' }} />}>
        <Link to="/shop">Admin Shop</Link>
      </Menu.Item>
      <Menu.Item key="6" icon={<LogoutOutlined style={{ fontSize: '1.6rem' }} />}>
        <Link to="/logout">Log Out</Link>
      </Menu.Item>
    </SubMenu>
    <CartDropDown />
  </Menu>
);

export default windowSize(RightNavigation);
