import React from 'react';
import { Link } from 'react-router-dom';
import { Menu } from 'antd';
import { LogoutOutlined, ShopOutlined, UserOutlined } from '@ant-design/icons';
import { signOutStart } from '../../redux/user/user.action';
import { connect } from 'react-redux';

const { SubMenu } = Menu;
const NavigationUser = ({currentUser,signOutStart,windowHeight,...props}) =>{ 


  return(
  <SubMenu icon={<UserOutlined />}  {...props}  title={<span style={{textTransform: 'capitalize'}}>{currentUser.name}</span>}>

    <Menu.Item key="4" icon={<UserOutlined style={{ fontSize: '1.6rem' }} />}>
      <Link to="/user">Your Profile</Link>
    </Menu.Item>
    <Menu.Item key="5" icon={<ShopOutlined style={{ fontSize: '1.6rem' }} />}>
      <Link to="/shop">Admin Shop</Link>
    </Menu.Item>
    <Menu.Item key="6" icon={<LogoutOutlined style={{ fontSize: '1.6rem' }} />}>
      <Link to="/" onClick = {() => signOutStart()}>Log Out</Link>
    </Menu.Item>
  </SubMenu>
)};

const mapDispatchToProps = dispatch => ({
  signOutStart : () => dispatch(signOutStart())
})

export default connect(null,mapDispatchToProps)(NavigationUser) ;
