import React from 'react';
import {connect} from 'react-redux';
import {
  Form, Input, Button,
} from 'antd';
import { MinusCircleOutlined, PlusOutlined } from '@ant-design/icons';
import { createStructuredSelector } from 'reselect';
import { selectCurrentUser } from '../../redux/user/user.selector';

const AddAddressUser = ({currentUser}) => (
  <Form 
  layout="horizontal" 
  style={{ flex: ' 0 0 50%' }} initialValues = {{address: [...currentUser.address]}}>
    <Form.Item>
      <Form.List name="address">
        {(fields, { add, remove }) => (
          <div>
            {fields.map((field) => (
              <>
                <Form.Item
                  label="Address"
                  key={field.key}
                  rules={[
                    { required: true, message: 'Missing Address name' },
                  ]}
                >
                  <Input placeholder="Address" style={{ width: '80%' }} />
                  <MinusCircleOutlined
                    onClick={() => {
                      remove(field.name);
                    }}
                  />
                </Form.Item>
              </>
            ))}

            <Form.Item>
              <Button
                type="dashed"
                onClick={() => {
                  add();
                }}
                block
              >
                <PlusOutlined />
                {' '}
                Add Address
              </Button>
            </Form.Item>
          </div>
        )}
      </Form.List>
    </Form.Item>
  </Form>
);


const mapStateToProps = createStructuredSelector({
  currentUser : selectCurrentUser
})

export default connect(mapStateToProps)(AddAddressUser) ;
