import React, { useState } from 'react';

import {
  Form, Input, Button, InputNumber, Layout,
} from 'antd';
import { MinusCircleOutlined, PlusOutlined } from '@ant-design/icons';

const { Content } = Layout;
const InformationUser = () => {
  const [componentSize, setComponentSize] = useState('default');

  const onFormLayoutChange = ({ size }) => {
    setComponentSize(size);
  };
  return (
    <Layout style={{ padding: '0 24px 24px' }}>
      <Content style={{
        padding: 24,
        margin: 0,
        minHeight: 600,
      }}
      >
          <div>
            <Form
            labelCol={{
                span: 4,
            }}
            wrapperCol={{
                span: 14,
            }}
            layout="horizontal"
            initialValues={{
                size: componentSize,
            }}
            onValuesChange={onFormLayoutChange}
            size={componentSize}
            >
            <Form.Item label="User Name">
                <Input />
            </Form.Item>
            <Form.Item label="InputNumber">
                <InputNumber style={{ width: '70%' }} />
            </Form.Item>
            <Form.List name="address">
                {(fields, { add, remove }) => (
                <div>
                    {fields.map((field) => (
                    <>
                        <Form.Item
                        label="Address"
                        {...field}
                        name={[field.name, 'address']}
                        fieldKey={[field.fieldKey, 'address']}
                        rules={[
                            { required: true, message: 'Missing Address name' },
                        ]}
                        >
                        <Input placeholder="Address" style={{ width: '80%' }} />
                        <MinusCircleOutlined
                            onClick={() => {
                            remove(field.name);
                            }}
                        />
                        </Form.Item>
                    </>
                    ))}

                    <Form.Item>
                    <Button
                        type="dashed"
                        onClick={() => {
                        add();
                        }}
                        block
                    >
                        <PlusOutlined />
                        {' '}
                        Add field
                    </Button>
                    </Form.Item>
                </div>
                )}
            </Form.List>
            <Form.Item>
                <Button>Update Me</Button>
            </Form.Item>
            </Form>
        </div>
      </Content>
    </Layout>
  );
};

export default InformationUser;
